use bevy::prelude::*;

#[derive(Clone, Copy, PartialEq, PartialOrd, Default)]
pub struct Space {
    pub width: f32,
    pub height: f32,
}

impl Space {
    pub fn ratio(self) -> f32 {
        self.width / self.height
    }
}
